CREATE TABLE CHAMADODB.CHAMADO (ID INTEGER GENERATED ALWAYS AS IDENTITY PRIMARY KEY, TITULO VARCHAR(20), STATUS BOOLEAN, DESCRICAO VARCHAR(100), DATA_INCLUSAO DATE, DATA_CONCLUSAO DATE)
CREATE TABLE CHAMADODB.DETALHE (ID INTEGER GENERATED ALWAYS AS IDENTITY PRIMARY KEY, COMENTARIO VARCHAR(200), CHAMADO_ID INTEGER REFERENCES CHAMADODB.CHAMADO(ID))